import 'package:flutter/cupertino.dart';
import 'package:flutter/material.dart';

class Country extends StatelessWidget{
  var Countr=['India','Britain','Canada','USA'];
  var City=['Delhi','London','Vancouver','New york'];
  var Image=['data:image/jpeg;base64,',
  'https://cdn.londonandpartners.com/-/media/images/london/visit/campaigns/international-recovery-campaign/lets-do-london-related-international-640.jpeg?mw=640&hash=477E6FF9F31F292EE017E5BA63027C7054EB39A0',
  'https://upload.wikimedia.org/wikipedia/commons/thumb/5/57/Concord_Pacific_Master_Plan_Area.jpg/1200px-Concord_Pacific_Master_Plan_Area.jpg',
  'https://www.travelandleisure.com/thmb/91pb8LbDAUwUN_11wATYjx5oF8Q=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/new-york-city-evening-NYCTG0221-52492d6ccab44f328a1c89f41ac02aea.jpg'];
  var prop=['Population: 19 mill','Population: 8 mill','Population: 2.4 mill','Population: 5.5 mill'];
  @override
  Widget build(BuildContext context) {
return Scaffold(
  appBar: AppBar(
    title: Text('Cities around world',style: TextStyle(fontWeight: FontWeight.bold,fontSize: 20),
    ),
  ),
  body: ListView.builder(itemCount: City.length,
      itemBuilder: (BuildContext,ind){
    return Card(
color: Colors.deepOrangeAccent,
      child: ListTile(
        isThreeLine: true,
        leading: CircleAvatar(
        backgroundImage:NetworkImage(Image[ind]),
        ),
        title: Text(City[ind],textAlign: TextAlign.center),
        subtitle: Text('${Countr[ind]} \n ${prop[ind]}',textAlign: TextAlign.center, ),
      ),
    );
    }
));
  }

}